extern byte unicode_info[];

struct UnicodeInfo {
	Index<dword>                      composed;
	Index<Tuple<dword, dword, dword>> decomposed;
	
	UnicodeInfo();
};

UnicodeInfo::UnicodeInfo()
{
	String ds = ZDecompress(unicode_info__, sizeof(unicode_info__));

	Vector<dword> data;
	dword prev = 0;
	StringStream ss(ds);
	while(!ss.IsEof()) {
		byte ch = ss.Get();
		dword val;
		if(ch < 255) {
			val = prev + ch - 127;
		}
		else {
			val = ss.Get();
			val |= ss.Get() << 8;
			val |= ss.Get() << 16;
			val |= ss.Get() << 24;
			val = prev + val - 127;
		}
		data.Add(val);
		prev = val;
	}
	
	int count = data[0];
	for(int i = 0; i < count; i++) {
		composed.Add(data[i + 1]);
		decomposed.Add(MakeTuple(data[i + count + 1], data[i + 2 * count + 1], data[i + 3 * count + 1]));
	}
	composed.Shrink();
	decomposed.Shrink();
}

int UnicodeDecompose(dword codepoint, dword t[MAX_DECOMPOSED])
{ // TODO: Add hangul support
	UnicodeInfo& f = Single<UnicodeInfo>();
	
	int q = f.composed.Find(codepoint);
	if(q >= 0) {
		const Tuple<dword, dword, dword>& d = f.decomposed[q];
		t[0] = d.a;
		t[1] = d.b;
		t[2] = d.c;
		return d.c ? 3 : 2;
	}
	t[0] = codepoint;
	return 1;
}

dword UnicodeCompose(dword *t, int count)
{
	if(count < 1 || count > 3)
		return 0;
	if(count == 1)
		return t[0];
	Tuple<dword, dword, dword> x;
	x.a = t[0];
	x.b = t[1];
	x.c = count == 3 ? t[2] : 0;
	UnicodeInfo& f = Single<UnicodeInfo>();
	int q = f.decomposed.Find(x);
	return q >= 0 ? f.composed[q] : 0;
}

